import React from "react";

function Form(props) {

    function handleSubmit(e) {
        e.preventDefault();
        alert('Hello, world!');
    }

    return(
        <form onSubmit={handleSubmit}>
            <h2 className="label-wrapper">
                <label htmlFor="new-task" >What needs to be done?</label>
            </h2>
            <input type="text" id="new-task" className="input_lg" name="new-task" autoComplete="off" placeholder="new task" maxLength="20">
            
            </input>
            <button type="submit" className="btn btn_lg add">
            Add
            </button>
        </form>
    )
}

export default Form;
